bool ShrubberyCreationForm::execute(Bureaucrat const & executor) const {
	if(executor.getGrade() <= this->_ReqExecutionGrade && this->_isSigned == true) {
		executeShrubberyCreation();
		return(true);
	}
	else {
		throw (Form::GradeTooLowException());
		return(false);
	}
}